import {Component, EventEmitter, Output} from '@angular/core';
import { AccountService } from '../services/account.service';
import RegisterDTO from '../DTOs/account/RegisterDTO';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {

  @Output() cancelRegister = new EventEmitter();
  model:RegisterDTO=new RegisterDTO();

  constructor(private accountService:AccountService) {

  }

  register() {
    console.log(this.model);
    this.accountService.register(this.model).subscribe(respose=>{
      console.log(respose);
      this.cancel();
    },error=>{
      console.log(error);
    })
  }

  cancel() {
    this.cancelRegister.emit(false);
  }

}
<div class="card">
  <div class="card-body">
    <form (ngSubmit)="register()">
      <h2 class="text-center text-primary">ثبت نام</h2>
      <hr>
      <div class="form-group mb-2">
        <input type="text" class="form-control" name="email" [(ngModel)]="model.email" placeholder="ایمیل">
      </div>

      <div class="form-group mb-2">
        <input type="password" class="form-control" name="password" [(ngModel)]="model.password" placeholder="کلمه عبور">
      </div>
      <div class="form-group mb-2">
        <input type="password" class="form-control" name="confirmPassword" [(ngModel)]="model.confirmPassword" placeholder="تکرار کلمه عبور">
      </div>

      <div class="form-group text-center">
        <button class="btn btn-success" type="submit">ثبت نام</button>
        <button class="btn btn-default ml-10px" (click)="cancel()" type="button">لغو</button>
      </div>
    </form>
  </div>
</div>
