import { Component, EventEmitter, OnInit, Output } from '@angular/core';
import { AccountService } from '../services/account.service';
import RegisterDTO from '../DTOs/account/RegisterDTO';
import { AbstractControl, FormControl, FormGroup, ValidatorFn, Validators } from '@angular/forms';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent implements OnInit {

  @Output() cancelRegister = new EventEmitter();
  model: RegisterDTO = new RegisterDTO();

  registerForm: FormGroup;

  constructor(private accountService: AccountService) {

  }

  ngOnInit(): void {
    this.initForm();
  }

  register() {
    console.log(this.model);
    this.accountService.register(this.model).subscribe(respose => {
      console.log(respose);
      this.cancel();
    }, error => {
      console.log(error);
    })
  }

  initForm() {
    this.registerForm = new FormGroup({
      email: new FormControl('', Validators.required),
      password: new FormControl('', [Validators.required, Validators.minLength(4), Validators.maxLength(25)]),
      confirmPassword: new FormControl('', [Validators.required, this.matchValues('password')])
    });
  }

  matchValues(matchTo: string):ValidatorFn {
    return (control: AbstractControl) => {
      console.log((control?.parent?.controls))
      return control?.value === (control?.parent?.controls as { [key: string]: AbstractControl })[matchTo].value ? null : { isMatching: true };
    }
  }

  cancel() {
    this.cancelRegister.emit(false);
  }

}
