import {Component} from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})

export class HomeComponent {
  registerMode: boolean = false;


  toggleRegisterMode() {
    this.registerMode = !this.registerMode;
  }

  cancelRegisterMode(event:boolean){
    this.registerMode=event;
  }

}
<div class="container mt-5">
  <div class="text-center" *ngIf="!registerMode">
    <h3>دوست خود را پیدا کنید</h3>
    <p>با استفاده از این صفحه میتوانید دوست خود را پیدا کنید ...</p>
  </div>
  <div class="text-center" *ngIf="!registerMode">
    <a class="btn btn-primary btn-lg" (click)="toggleRegisterMode()">ثبت نام</a>
    <button class="btn btn-info btn-lg ml-10px">نمایش بیشتر ...</button>
  </div>
  <div class="container"  *ngIf="registerMode">
    <div class="row justify-content-center">
      <div class="col-4">
        <app-register (cancelRegister)="cancelRegisterMode($event)"></app-register>
      </div>
    </div>
  </div>
</div>
